import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './HomeBody.css'
import ReadMore from './ReadMore';
function HomeBody() {
    const [blogs, setBlogs] = useState([]);
    const [blog, setBlog] = useState({});

    
    useEffect(() => {
        fetchRecords();
    }, []);

    const getCategory = () => {
        axios.get("http://localhost:8080/catagory")
            .then((response) => {
                setCategories(response.data);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    };
    
    const fetchRecords = () => {
        axios.get("http://localhost:8080/blog")
            .then((response) => {
                setBlogs(response.data);
                setBlog(response.data[0]);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    };

    return (
        <div className="hd">
            <br></br>
            <br></br>
            <div className='category'>
                <a class="nav-link"  href="/addpost">Add post</a>
            </div>
            <div className="main row row-cols-1 row-cols-md-3 g-4">
                {
                blogs.map((blogItem, index) => (
                    <div className="col" key={index}>
                    <div className="card" id={blogItem.id} >
                        <div className="card-body">
                            <h5 className="card-title">{blogItem.title}</h5>
                            <p className="card-text">
                                <ReadMore text={blogItem.body}/></p>
                        </div>
                    </div>
                </div>
                ))}
            </div>
        </div>
    );
}

export default HomeBody;
