import { useEffect, useState } from "react";
import axios from "axios";
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";


function Register() {
    const history = useHistory();
    const [user, setUser] = useState({ firstName: "",lastName: "", email: "", password: "" })
    const [message, setMessage]=useState("");
    const url = "http://localhost:8080/login/register";

    const OnTextChange = (args) => {
        var user1 = { ...user }
        user1[args.target.name] = args.target.value;
        setUser(user1);
    }
    const showMessage=(msgText)=>{
        setMessage(msgText);
        window.setTimeout(()=>{
            setMessage("");
        },3000);
    }
    const AddUser = () => {
        axios.post(url, user)
            .then((result) => {
                if (result.data != null) {
                    console.log(result);
                    showMessage("Registered Successfully!!");
                    history.push("/login");
                }
            })
            .catch((error) => {
                console.error('Error adding user:', error);
                if (error.response) {
                    
                    console.error('Response data:', error.response.data);
                    console.error('Response status:', error.response.status);
                    console.error('Response headers:', error.response.headers);
                } else if (error.request) {
                    console.error('No response received. Request:', error.request);
                } else {
                    console.error('Error setting up the request:', error.message);
                }
                showMessage("Error registering user. Please try again.");
            });
    };
    

    // const ClearBoxes = ()=>{
    //     setUser({ name: "", emailId: "", phoneNo: "", password: "" });
    // }

    return (<div className="outcontainer">
    <div className="container">
        <div className="form">
            <h2>Register</h2>
            <div className="box1">
                <input type="text" name="firstName" placeholder="Email" value={user.email} onChange={onTextChange}></input>

                <input type="password" name="password" placeholder="Password" value={user.password} onChange={onTextChange}></input>
            </div>
            <div className="box1">
                <input type="email" name="email" placeholder="Email" value={user.email} onChange={onTextChange}></input>

                <input type="password" name="password" placeholder="Password" value={user.password} onChange={onTextChange}></input>
            </div>
                <h6><Link to="/changepass">Change Password</Link></h6>
            <div className="box2">

                <button className="btn" type="submit" onClick={handleLogin}>Login</button>
            </div>
        </div>
    </div>
</div>)
}

export default Register;
