import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './HomeBody.css'
import ReadMore from './ReadMore';

function HomeBody() {
    const [blogs, setBlogs] = useState([]);

    useEffect(() => {
        fetchRecords();
    }, []);

    const fetchRecords = () => {
        axios.get("http://localhost:8080/blog")
            .then((response) => {
                setBlogs(response.data);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    };

    const deleteBlog = (id) => {
        axios.delete(`http://localhost:8080/blog/${id}`)
            .then((response) => {
                console.log(response.data);
                // Assuming you want to update the state after deleting the blog
                setBlogs(prevBlogs => prevBlogs.filter(blog => blog.id !== id));
            })
            .catch((error) => {
                console.error('Error deleting blog:', error);
            });
    };

    return (
        <div className="hd">
            <br />
            <br />
            <div className='category'>
                <li class="nav-item">
                    <a class="nav-link" href="/addpost">Add post</a>
                </li>
            </div>
            <div className="main row row-cols-1 row-cols-md-3 g-4">
                {blogs.map((blogItem, index) => (
                    <div className="col" key={index}>
                        <div className="card" id={blogItem.id} >
                            <div className="card-body">
                                <h5 className="card-title">{blogItem.title}</h5>
                                <p className="card-text">
                                    <ReadMore text={blogItem.body}/>
                                </p>
                                <button className='btn btn-secondary' onClick={() => deleteBlog(blogItem.id)}>Delete</button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default HomeBody;
