import React, { useEffect, useState } from 'react';
import axios from 'axios';
import backpack from "./images/backpack.jpg";
import disney from "./images/disney.jpg";
import gateway from "./images/gateway.jpg";
import himalaya from "./images/himalaya.jpg";
import hogwarts from "./images/hogwarts.jpg";
import maldives from "./images/maldives.jpg";
import meghalaya from "./images/meghalaya.jpg";
import punetrek from "./images/punetrek.jpg";
import south from "./images/south.jpg";


function HomeBody() {
    const [blogs, setBlogs] = useState([]);
    const [blog, setBlog] = useState({});
    const [img,setImg]=useState({backpack,disney,gateway,himalaya,hogwarts,maldives,meghalaya,punetrek,south})

    useEffect(() => {
        fetchRecords();
    }, []);

    const fetchRecords = () => {
        axios.get("http://localhost:8080/blog")
            .then((response) => {
                setBlogs(response.data);
                setBlog(response.data[0]);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    };

    return (
        <div>
            <div className="row row-cols-1 row-cols-md-3 g-4">
                {blogs.map((blogItem, index) => (
                    <div className="col" key={index}>
                        <div className="card">
                            <img src={img} className="card-img-top" alt={blogItem.title} />
                            <div className="card-body">
                                <h5 className="card-title">{blogItem.title}</h5>
                                <p className="card-text">{blogItem.body}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default HomeBody;
