import axios from 'axios';
import React, { useState, useEffect } from 'react';

function AddPostBody() {
    const [categories, setCategories] = useState([]);
    const[blog,setBlog]=useState({id:"",title:"",body:"",categoryId:""});

    useEffect(() => {
        getCategory();
    }, []); 

    const getCategory = () => {
        debugger;
        axios.get("http://localhost:8080/catagory")
            .then((response) => {
                setCategories(response.data);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    };
    const onTextChange = (args) => {
        var blog1 = { ...blog };
        blog1[args.target.name] = args.target.value;
        setBlog(blog1);
    }

    const handleSubmit = (event) => {
        event.preventDefault();
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="title" className="form-label">Blog title</label>
                    <input type="text" className="form-control" id="title" aria-describedby="title" style={{ height: '40px', width:'600px' }}/>
                </div>
                <div className="form-floating">
                    <textarea className="form-control" placeholder="Enter blog content" id="body" style={{ height: '200px', width:'600px' }}></textarea>
                    <label htmlFor="body">Enter blog content</label>
                </div>
                <br></br>
                <select className="form-select" aria-label="Default select example"style={{ height: '40px', width:'600px' }}>
                     {categories.map((catItem) => (
                     <option key={catItem.id} value={catItem.id}>{catItem.catagoryName}</option>
                     ))}
                </select>
                <br></br>
                <button type="submit" className="btn btn-primary">Post</button>
            </form>
        </div>
    );
}

export default AddPostBody;
