import axios from 'axios';
import React, { useState, useEffect } from 'react';

function AddPostBody() {
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        getCategory();
    }, []); // Empty dependency array to ensure it only runs once on mount

    const getCategory = () => {
        debugger;
        axios.get("http://localhost:8080/category/categories")
            .then((response) => {
                setCategories(response.data);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="title" className="form-label">Blog title</label>
                    <input type="text" className="form-control" id="title" aria-describedby="title" />
                </div>
                <div className="form-floating">
                    <textarea className="form-control" placeholder="Enter blog content" id="body" style={{ height: '100px' }}></textarea>
                    <label htmlFor="body">Enter blog content</label>
                </div>
                <select className="form-select" aria-label="Default select example">
                     {categories.map((catItem) => (
                     <option key={catItem.Id} value={catItem.catagoryId}>{catItem.catagoryName}</option>
                     ))}
                </select>
                <button type="submit" className="btn btn-primary">Post</button>
            </form>
        </div>
    );
}

export default AddPostBody;
