import axios from 'axios';
import React, { useState } from 'react';

function AddPostBody() {
    const[category,setCat]=useState([])
    const handleSubmit = (event) => {
        event.preventDefault();
        
    };
    const getCategory = () => {
        axios.get("http://localhost:8080/catagory/categories")
            .then((response) => {
               setCat(response.data);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    };


    return (
        <div  onLoad={getCategory}>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="title" className="form-label">Blog title</label>
                    <input type="text" className="form-control" id="title" aria-describedby="title" />
                </div>
                <div className="form-floating">
                    <textarea className="form-control" placeholder="Enter blog content" id="body" style={{ height: '100px' }}></textarea>
                    <label htmlFor="body">Enter blog content</label>
                </div>
                <select className="form-select" aria-label="Default select example">
                    <option defaultValue>Categories</option>
                    {blogs.map((blogItem, index) => (
                    <div className="col" key={index}>
                    <div className="card" id={blogItem.id} >
                        <div className="card-body">
                            <h5 className="card-title">{blogItem.title}</h5>

                            <p className="card-text">
                                <ReadMore text={blogItem.body}/></p>
                        </div>
                    </div>
                </div>
                ))}
                    <option value="1">One</option>
                    
                </select>
                <button type="submit" className="btn btn-primary">Post</button>
            </form>
        </div>
    );
}

export default AddPostBody;
