import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './HomeBody.css'
import ReadMore from './ReadMore';
function HomeBody() {
    const [blogs, setBlogs] = useState([]);
    const [blog, setBlog] = useState({});

    
    useEffect(() => {
        fetchRecords();
    }, []);


    
    const fetchRecords = () => {
        axios.get("http://localhost:8080/blog")
            .then((response) => {
                setBlogs(response.data);
                setBlog(response.data[0]);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    };

    return (
        <div className="home-container">
    <br/>
    <br/>
    <div className='navigation'>
        <ul className="nav-list">
            <li className="nav-item">
                <a className="nav-link" href="/addpost">Add Post</a>
            </li>
        </ul>
    </div>
    <div className="main-content">
        <div className="row row-cols-1 row-cols-md-3 g-4">
            {blogs && blogs.map(blogItem => (
                <div className="col" key={blogItem.id}>
                    <div className="card" id={blogItem.id}>
                        <div className="card-body">
                            <h5 className="card-title">{blogItem.title}</h5>
                            <p className="card-text">
                                <ReadMore text={blogItem.body} />
                            </p>
                        </div>
                    </div>
                </div>
            ))}
            {!blogs && (
                <div className="col">
                    <p>No blogs available</p>
                </div>
            )}
        </div>
    </div>
</div>
    )
}

export default HomeBody;
